Atributos.Comando:
self.Itens.ProdutoFinal->collect( pf | 
  pf.SaldosProdutos->select( sp | sp.Unidade = self.UnidadeDestino )->first.UltimoCusto := pf.SaldosProdutos->select( sp | sp.Unidade = self.Unidade )->first.UltimoCusto
)

let cme = self->first in (
  cme.MovimentosDependentes.delete
  ; cme.CriarMovimentoDestino(self.UnidadeDestino)

  ; cme.NumeroPadrao := cme.MovimentosDependentes->select( md | md.NumeroPadrao->notEmpty )->first.NumeroPadrao
  ; cme.UpdateLastUndoBlock->Refresh
  ; if cme.NumeroPadrao->isEmpty then cme.GeraNumeradoresAutomaticos(cme.TipoMovimentoEstoque) else false endif
  ;
  cme.MovimentosDependentes->collect( cmeMD |
    let newCMEN = CabMovEstoqueNumero.Create in (
      newCMEN.Movimento := cmeMD
      ; newCMEN.Numero := cmeMD.MovimentoTitular.NumeroPadrao.Numero
      ; cmeMD.NumeroPAdrao := newCMEN
    )
  )
)

let cme = self->first in (
  cme.MovimentosDependentes.delete
  ; cme.CriarMovimentoDestino(self.UnidadeDestino)

  ; cme.NumeroPadrao := cme.MovimentosDependentes->select( md | md.NumeroPadrao->notEmpty )->first.NumeroPadrao
  ; cme.UpdateLastUndoBlock->Refresh
  ; if cme.NumeroPadrao->isEmpty then cme.GeraNumeradoresAutomaticos(cme.TipoMovimentoEstoque) else false endif
  ;
  cme.MovimentosDependentes->collect( cmeMD |
    let newCMEN = CabMovEstoqueNumero.Create in (
      newCMEN.Movimento := cmeMD
      ; newCMEN.Numero := cmeMD.MovimentoTitular.NumeroPadrao.Numero
      ; cmeMD.NumeroPAdrao := newCMEN
    )
  )
)

self.Itens.ProdutoFinal->collect( pf | 
  pf.SaldosProdutos->select( sp | sp.Unidade = self.UnidadeDestino )->first.UltimoCusto := pf.SaldosProdutos->select( sp | sp.Unidade = self.Unidade )->first.UltimoCusto
)
